SELECT COUNT(DISTINCT pp.`project_ID`) AS guarantyProCount 
FROM pro_project pp 
LEFT JOIN (
SELECT SUM(IFNULL(pfp.`payCapitalSum`,0)) AS payCapitalSum,pfp.`project_ID` FROM pro_factpay pfp WHERE 1=1 ${has(endyearmonthsql)?(" and  DATE_FORMAT(pfp.`payDate`,'%Y-%m')<'"+endyearmonthsql+"'"):""}   GROUP BY pfp.`project_ID`
) tab ON tab.project_ID = pp.project_ID
LEFT JOIN (
SELECT SUM(IFNULL(pr.`replaceCapitalSum`,0)) AS replaceCapitalSum,pr.`project_ID` FROM pro_replace pr WHERE 1=1 ${has(endyearmonthsql)?(" and  DATE_FORMAT(pr.`replaceDate`,'%Y-%m')<'"+endyearmonthsql+"'"):""}  GROUP BY pr.`project_ID`
) tabRe ON tabRe.project_ID = pp.project_ID
WHERE pp.`busiClass` = '01'
-- AND (loadSum -IFNULL(tab.payCapitalSum,0) - IFNULL(tabRe.replaceCapitalSum,0)) >0
 AND   (pp.finishDate is null  ${has(endyearmonthsql)?(" or DATE_FORMAT(pp.finishDate ,'%Y') >=DATE_FORMAT('"+(endyearmonthsql+'-'+'0'+'1')+"','%Y')"):""} )
${has(endyearmonthsql)?(" and  DATE_FORMAT(pp.`reportDate`,'%Y-%m')<'"+endyearmonthsql+"'"):""} 
${has(wheresql)?(" and wheresql='"+wheresql+"'"):""}
${has(dataJurisdictionSql)?(dataJurisdictionSql):""}
